import React from "react";
// import CalculatorTitle from "./calculatorTitle.js";
// import OutputScreen from "./outputScreen.js";
import Button from "./button.js";
import { useEffect } from "react";

class App extends React.Component {
  constructor() {
    super();
  }

  componentDidMount() {
    console.log("did mount");
    let x = Array.from(document.getElementsByClassName("mainCalc"));
    x.map((buton) => {
      buton.addEventListener("click", (e) => {
        console.log("---------->", e.target.innerText);
      });
    });
  }

  // our method to handle all click events from our buttons

  render() {
    return (
      <div className="frame container">
        <div id="display"></div>
        {/* <CalculatorTitle value="Calculator App" /> */}
        <div className="mainCalc">
          {/* <OutputScreen /> */}
          <div className="button-row">
            <Button className="button" id="clear" label={"C"} />
            <Button className="button" label={"/"} />
            <Button className="button" label={"*"} />
            <Button className="button" label={"&larr"} />
          </div>
          <div className="button-row">
            <Button className="button" label={"7"} />
            <Button className="button" label={"8"} />
            <Button className="button" label={"9"} />
            <Button className="button" label={"-"} />
          </div>
          <div className="button-row">
            <Button className="button" label={"4"} />
            <Button className="button" label={"5"} />
            <Button className="button" label={"6"} />
            <Button className="button" label={"+"} />
          </div>
          <div className="button-row">
            <Button className="button" label={"1"} />
            <Button className="button" label={"2"} />
            <Button className="button" label={"3"} />
            <Button className="button" label={"."} />
          </div>
          <div className="button-row">
            <Button className="button" label={"("} />
            <Button className="button" label={"0"} />
            <Button className="button" label={")"} />
            <Button id="equal" className="button" label={"="} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
