import React from "react";
// import CalculatorTitle from "./calculatorTitle.js";
// import OutputScreen from "./outputScreen.js";
import { useEffect } from "react";

function App() {
  useEffect(() => {
    let display = document.getElementById("display");
    let x = Array.from(document.getElementsByClassName("mainCalc"));
    x.map((buton) => {
      buton.addEventListener("click", (e) => {
        display.innerText += e.target.innerText;
      });
    });
  }, [0]);

  // our method to handle all click events from our buttons

  return (
    <div className="container">
      <div id="display"></div>
      {/* <CalculatorTitle value="Calculator App" /> */}
      <div className="mainCalc">
        {/* <OutputScreen /> */}
        <div className="button" id="clear">
          C
        </div>
        <div className="button" label={"/"}>
          /
        </div>
        <div className="button" label={"*"}>
          *
        </div>
        <div className="button" label={"&larr"}>
          &larr;
        </div>
        <div className="button" label={"7"}>
          7
        </div>
        <div className="button" label={"8"}>
          8
        </div>
        <div className="button" label={"9"}>
          9
        </div>
        <div className="button" label={"-"}>
          -
        </div>
        <div className="button" label={"4"}>
          4
        </div>
        <div className="button" label={"5"}>
          5
        </div>
        <div className="button" label={"6"}>
          6
        </div>
        <div className="button" label={"+"}>
          +
        </div>
        <div className="button" label={"1"}>
          1
        </div>
        <div className="button" label={"2"}>
          2
        </div>
        <div className="button" label={"3"}>
          3
        </div>
        <div className="button" label={"."}>
          .
        </div>
        <div className="button" label={"("}>
          (
        </div>
        <div className="button" label={"0"}>
          0
        </div>
        <div className="button" label={")"}>
          )
        </div>
        <div id="equal" className="button" label={"="}>
          =
        </div>
      </div>
    </div>
  );
}

export default App;
